tinymce.PluginManager.add("filemanager", function (a, b) {
    a.addButton("filemanager", {
        
        icon: 'browse',
        onclick: function () {
            a.windowManager.open({
                title: "File Manager",
                url: a.settings.filemanager_path + (a.settings.filemanager_path.indexOf('?') > -1 ? '&opener=tinymce' : '?opener=tinymce'),
                width: 1024,
                height: 530
            })
        }
    })    
});